      <ul>{showCategories()}</ul>
      <div className="buttons-row">
        <Link to="/attractions">
          <button>Previous: Attractions</button>
        </Link>
        <Link to="/myexperience">
          <button>Finish Up: My Experience</button>
        </Link>
      </div>
    </div>
  );
}

